import { useState } from 'react';
import { TextInput, View , StyleSheet, Alert} from'react-native';
import PrimaryButton from '../components/primarybutton';
function Startgamesscreen( onpicknumber ) { 
   const [enternumber, setenterdnumber] =useState('')
   function numberinputhandeler(enteredText){
    setenterdnumber(enteredText)
   }
   function resetInputhandeler(){
    setenterdnumber('');
   }
   function confirmInputhandeler (){
    const chosenNumber = parseInt(enternumber);
    console.log(chosenNumber)

    if (isNaN(chosenNumber) || chosenNumber<=0 || chosenNumber > 99  ){
        Alert.alert('Invailid number','Number has to be a number between 1 and 99', [{Text:'okay',style:'destructive',onPress: resetInputhandeler}] )
        return;
    }
        onpicknumber(chosenNumber);
}
   
   return (
    <View style={styles.inputContainer}>
        <TextInput style={styles.numberInput} onChangeText={numberinputhandeler} maxLength={2} keyboardType="number-pad" value={enternumber}/>
        <View style={styles.buttonscontainer}>
            <View style={styles.buttoncontaner}>
                <PrimaryButton onpress={resetInputhandeler}> reset </PrimaryButton>
            </View>
            <View style={styles.buttoncontaner}>
                <PrimaryButton onpress={confirmInputhandeler} > confirm </PrimaryButton>
             </View>
        </View>
    </View>
    );
}

export default Startgamesscreen; 
const styles = StyleSheet.create({
inputContainer :{
    justifyContent:'center',
    alignItems:'center',
    padding:16,
    marginHorizontal:24,
    borderRadius:8,
    marginTop:100,
    backgroundColor:'#3b021f',
    elevation:4,
},
numberInput :{
    height: 50,
    width:50,
    fontSize:32,
    borderBottomColor:'#ddb52f',
    borderBottomWidth:1,
    color:'#ddb52f',
    marginVertical:8,
    fontWeight: 'bold',
    textAlign:'center',
},
buttonscontainer:{
    flexDirection:'row',
}, 
buttoncontaner:{
    flex:1,
}
}
)
